    private void createResumeButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_createResumeButtonActionPerformed
        // TODO add your handling code here:

        if(firstNameText.getText().equals("") || emailText.getText().equals("") || phoneNoText.getText().equals("") || streetLn1Text.getText().equals("") || countryCombobox.getSelectedItem().toString().equals("Select Country") || cityText.getText().equals("") || stateText.getText().equals("") || zipCodeText.getText().equals("") )
        {
            JOptionPane.showMessageDialog(this, "Mandatory fields can't be empty","warning",JOptionPane.WARNING_MESSAGE);
            firstNameText.grabFocus();
        }
        else
        {
            Resume resume = new Resume();
            try{
                resume.setPhoneNo(Long.parseLong(phoneNoText.getText()));
                resume.setZipCode(Integer.parseInt(zipCodeText.getText()));
            }
            catch(Exception e)
            {
                JOptionPane.showMessageDialog(this, "PhoneNo and Zipcode can only be numbers","Warning",JOptionPane.WARNING_MESSAGE);
                return;
            }
            try{

                String name;
                String street;
                String comp1Date;
                String comp2Date;
                String cityStateCountry;
                String skills;

                name = firstNameText.getText() + " " + lastNameText.getText();
                resume.setName(name);
                resume.setEmail(emailText.getText());
                resume.setStreetLn1(streetLn1Text.getText());
                resume.setStreetLn2(streetLn2Text.getText());
                street = streetLn1Text.getText() + ", " + streetLn2Text.getText();
                resume.setStreet(street);
                resume.setCity(cityText.getText());
                resume.setState(stateText.getText());
                resume.setCountry(countryCombobox.getSelectedItem().toString());
                cityStateCountry = resume.getCity() + ", " + resume.getState() + ", " + resume.getCountry();
                resume.setCityStateCountry(cityStateCountry);

                if(maleRb.isSelected()){
                    resume.setSex(maleRb.getText());
                }
                else if(femaleRb.isSelected()){
                    resume.setSex(femaleRb.getText());
                }
                resume.setCarrierObjStmt(cosTextArea.getText());
                resume.setPgCollegeName(pgNameText.getText());
                resume.setPgCourse(pgCourseText.getText());
                resume.setPgDate(pgEndDateText.getText());
                resume.setUgCollegeName(ugNameText.getText());
                resume.setUgCourse(ugCourseText.getText());
                resume.setUgDate(ugEndDateText.getText());
                resume.setUgPerc(ugPercText.getText());
                resume.setPgPerc(pgPercText.getText());

                resume.setComp1Name(comp1NameText.getText());
                resume.setComp1Role(comp1RoleText.getText());
                resume.setComp1StartDate(comp1StartDateText.getText());
                resume.setComp1EndDate(comp1EndDateText.getText());
                Boolean comp1BoolDate = comp1StartDateText.getText().isEmpty();
                if (comp1BoolDate.equals(false)){
                    comp1Date = resume.getComp1StartDate() + " - " + resume.getComp1EndDate();
                    resume.setComp1Date(comp1Date);
                }

                resume.setComp2Name(comp2NameText.getText());
                resume.setComp2Role(comp2RoleText.getText());
                resume.setComp2StartDate(comp2StartDateText.getText());
                resume.setComp2EndDate(comp2EndDateText.getText());
                Boolean comp2BoolDate = comp2StartDateText.getText().isEmpty();
                if (comp2BoolDate.equals(false)){
                    comp2Date = resume.getComp2StartDate() + " - " + resume.getComp2EndDate();
                    resume.setComp2Date(comp2Date);
                }

                resume.setAffiliation(affiliationsText.getText());
                resume.setProjects(projectsText.getText());
                skills = skillCheckbox();
                resume.setSkills(skills);
                resume.setExtraCurricular(extraActText.getText());
                resume.setImg(image);

                JOptionPane.showMessageDialog(null, "Resume has been created sucessfully!");

                resumeHistory.addResume(resume);
                clearFields();

            }
            catch (Exception e){
                JOptionPane.showMessageDialog(null, "Error in Creating Resume! Please check all fields","Error",JOptionPane.ERROR_MESSAGE);
                firstNameText.grabFocus();
                
            }
        }
    }//GEN-LAST:event_createResumeButtonActionPerformed

    private void uploadPicButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_uploadPicButtonActionPerformed
        // TODO add your handling code here:

        JFileChooser imageChooser = new JFileChooser();
        FileNameExtensionFilter imageFilter = new FileNameExtensionFilter( "JPG Images","jpg");
        imageChooser.setFileFilter(imageFilter);
        imageChooser.addChoosableFileFilter(imageFilter);
        imageChooser.setAcceptAllFileFilterUsed(false);
        imageChooser.showOpenDialog(null);
        String imgFilePath;
        imgFilePath = imageChooser.getSelectedFile().getAbsolutePath();
        Image img;
        Image scaledImg;
        try {
            img = ImageIO.read(new File(imgFilePath));
            image = img.getScaledInstance(110, 110, Image.SCALE_AREA_AVERAGING);

            picImage.setIcon(new ImageIcon(image));

        }

        catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Cannot Upload");

        }
    }//GEN-LAST:event_uploadPicButtonActionPerformed

    
