    private String skillCheckbox()
    {
        StringBuilder skills = new StringBuilder();
        if(javaCb.isSelected())
                {
                    skills.append(javaCb.getText()).append("\r\n");
                }
                if(cPlusCb.isSelected())
                {
                    skills.append(cPlusCb.getText()).append("\r\n");
                }
                if(vbNetCb.isSelected())
                {
                    skills.append(vbNetCb.getText()).append("\r\n");
                }
                if(plSqlCb.isSelected())
                {
                    skills.append(plSqlCb.getText()).append("\r\n");
                  
                }
                if(htmlCb.isSelected())
                {
                    skills.append(htmlCb.getText()).append("\r\n");
                }
                if(otherCb.isSelected())
                {                
                    skills.append(skillText.getText());
                }
                
                return skills.toString();
    }   

